require 'spec_helper'

describe StarsController do

  # This should return the minimal set of attributes required to create a valid
  # Movie. As you add validations to Movie, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) { { "name" => "Sam Spade", "bio" => "He was a private dick", "headshot_url" => "data:image/jpeg;base64," } }

    describe "GET show" do
    it "assigns the requested star as @star" do
      star = Star.create valid_attributes
      get :show, {:id => star}
      expect(assigns(:star)).to eq(star)
    end
  end

end
